const routes={"/":()=>"<h1>Welcome to Findthinger</h1><p>Plan your next road trip!</p>","/search":()=>"<h1>Search</h1><p>Find destinations here (coming soon).</p>","/about":()=>"<h1>About</h1><p>A vanilla JS road trip planner.</p>",404:()=>"<h1>404 - Not Found</h1><p>Oops, wrong turn!</p>"};function router(){const app=document.getElementById("app");const path=window.location.hash.slice(1)||"/";const content=routes[path]||routes["404"];app.innerHTML=content()}window.addEventListener("hashchange",router);document.addEventListener("DOMContentLoaded",router);